import pytest


def main(has_towers, k):
    n = len(has_towers)
    tower_coords = [i for i, has_tower in enumerate(has_towers, 1) if has_tower] + [n + k]
    left_uncovered = 1
    previous_tower = None
    total = 0
    for tower_coord in tower_coords:
        # This tower can't cover the leftmost uncovered city
        while left_uncovered <= tower_coord - k:
            # No one to cover
            if not previous_tower:
                return -1
            total += 1
            left_uncovered = previous_tower + k
            previous_tower = None
        previous_tower = tower_coord
    return total

